  renderTable('salesTable',['# Orden','Fecha','Canal','RegiÃ³n','SKU','Producto','Unidades','Monto'],sRows.map(r=>[r.order_id,r.date,r.channel,r.region,r.sku,r.product,r.units,r.amount]));
