function setRange(preset){
  const now = new Date();
  let from = new Date(now);
  if(preset === 'ytd'){ from = new Date(now.getFullYear(),0,1); }
  else { from.setDate(now.getDate() - parseInt(preset)); }
  ui.els.to.value = now.toISOString().slice(0,10);
  ui.els.from.value = from.toISOString().slice(0,10);
}
