// Helpers
function convert(amount, currency){ const rates = { USD:1, EUR:0.92, COP:4000 }; return amount * (rates[currency] || 1); }
function between(dateStr, fromStr, toStr){ const d = new Date(dateStr); return (!fromStr || d >= new Date(fromStr)) && (!toStr || d <= new Date(toStr)); }
function slice(){
  const f = ui.els.from.value; const t = ui.els.to.value; const ch = document.getElementById('channel').value; const rg = document.getElementById('region').value;
  let rows = state.timeseries.filter(r => between(r.date, f, t));
  if(ch !== 'all') rows = rows.filter(r => r.channel === ch);
  if(rg !== 'all') rows = rows.filter(r => r.region === rg);
  return rows;
}
function pctl(arr, p){ if(!arr.length) return 0; const a = [...arr].sort((x,y)=>x-y); const i = Math.floor((a.length-1)*(p/100)); return a[i]; }

// KPIs
function renderKPIs(){
  const rows = slice();
  const curr = document.getElementById('currency').value;
  let revenue = rows.reduce((a,r)=>a+convert(r.revenue,curr),0);
  let cost = rows.reduce((a,r)=>a+convert(r.cost,curr),0);
  let margin = revenue - cost;
  let gm = revenue ? (margin/revenue)*100 : 0;
  let units = rows.reduce((a,r)=>a + r.units,0);
  const inv = state.invoices.filter(r => between(r.date, ui.els.from.value, ui.els.to.value));
  const ar = inv.filter(i => i.status !== 'Pagada').reduce((a,r)=>a+convert(r.amount,curr),0);
  const cogs = cost; const avgInventory = 50000; const turns = avgInventory ? (cogs / avgInventory) : 0;

  const kpis = [
    { title:'Ingresos', value:revenue.toLocaleString('es-ES') },
    { title:'Margen Bruto', value: gm.toFixed(1)+'%' },
    { title:'Unidades', value: units.toLocaleString('es-ES') },
    { title:'Cuentas por cobrar', value: ar.toLocaleString('es-ES') },
    { title:'Giros de inventario', value: turns.toFixed(2) },
    { title:'Stockouts', value: state.inventory.filter(i=>i.status==='Stockout').length+'' }
  ];

  ui.kpis.innerHTML = kpis.map((k,i)=>`
    <div class="kpi">
      <h4>${k.title}</h4>
      <div class="value">${k.value}</div>
      <div class="spark" id="spark${i}"></div>
    </div>
  `).join('');

  const dateAgg = aggregateByDate(rows, curr);
  const values = Object.values(dateAgg).map(v=>v.revenue);
  document.querySelectorAll('.spark').forEach((el)=>{ el.innerHTML = spark(values); });
}

// Aggregations
function aggregateByDate(rows, curr){
  const map = {};
  rows.forEach(r=>{
    map[r.date] ??= { revenue:0, units:0 };
    map[r.date].revenue += convert(r.revenue, curr);
    map[r.date].units += r.units;
  });
  return map;
}
function topNProducts(rows, n, curr){
  const m = new Map();
  rows.forEach(r=>{
    const k = r.product;
    m.set(k, (m.get(k)||0)+convert(r.revenue,curr));
  });
  return Array.from(m.entries()).sort((a,b)=>b[1]-a[1]).slice(0,n);
}
function inventoryBuckets(){
  const m = { OK:0, Bajo:0, Stockout:0 };
  state.inventory.forEach(i=> m[i.status]++);
  return m;
}
function agingBuckets(){
  const m = { "0-30":0, "31-60":0, "61-90":0, ">90":0 };
  state.invoices.forEach(inv => m[inv.aging_bucket]++);
  return m;
}

function gradient(ctx, c1, c2){ const g = ctx.createLinearGradient(0,0,0,220); g.addColorStop(0,c1); g.addColorStop(1,c2); return g; }
function baseOptions(maxY){
  const style = document.getElementById('vizStyle')?document.getElementById('vizStyle').value:'modern';
  return {
    responsive:true,
    maintainAspectRatio:false,
    animation: { duration: document.getElementById('animToggle').checked ? 450 : 0 },
    plugins: {
      legend: { display: style!=='minimal', labels: { color: getTextColor() } },
      tooltip: { enabled: true, backgroundColor: 'rgba(8,26,64,.92)' }
    },
    scales: {
      x: { ticks: { color: getTextColor() }, grid: gridStyle(style) },
      y: { beginAtZero:true, suggestedMax: maxY, ticks: { color: getTextColor() }, grid: gridStyle(style) }
    },
    elements: { line: { borderWidth:2 }, point: { radius:0 } },
  };
}
function getTextColor(){
  return getComputedStyle(document.documentElement).getPropertyValue('--text').trim();
}
function gridStyle(style){
  if(style==='contrast') return { color: 'rgba(255,255,255,.2)' };
  if(style==='minimal') return { display:false };
  return { color:'rgba(255,255,255,.08)' };
}

function renderCharts(){
  const rows = slice();
  const curr = document.getElementById('currency').value;
  const pal = window.currentPalette;

  const byDate = aggregateByDate(rows, curr);
  const labels = Object.keys(byDate);
  const revs = Object.values(byDate).map(v=>v.revenue);
  const units = Object.values(byDate).map(v=>v.units);

  const top = topNProducts(rows, 6, curr);
  const topLabels = top.map(x=>x[0]);
  const topVals = top.map(x=>x[1]);

  const inv = inventoryBuckets();
  const aging = agingBuckets();

  const maxSafe = Math.round(pctl(revs,95)*1.1);

  // Destroy previous
  Object.values(ui.charts).forEach(ch => ch.destroy && ch.destroy());

  // Revenue chart
  const revType = document.getElementById('revenueType').value;
  const ctxRev = document.getElementById('revenueChart').getContext('2d');
  const areaGrad = gradient(ctxRev, pal[0]+'BB', pal[0]+'10');
  let revConfig;
  if(revType==='area') {
    revConfig = {
      type:'line',
      data: { labels, datasets:[{ data: revs, fill:true, borderColor: pal[0], backgroundColor: areaGrad }] },
    };
  } else if(revType==='line') {
    revConfig = { type:'line', data: { labels, datasets:[{ data: revs, borderColor: pal[0] }] } };
  } else if(revType==='bar') {
    revConfig = { type:'bar', data: { labels, datasets:[{ data: revs, backgroundColor: pal[0] }] } };
  } else if(revType==='stacked') {
    revConfig = {
      type:'bar',
      data: { labels, datasets:[
        { label:'Ingresos', data: revs, backgroundColor: pal[0] },
        { label:'Unidades', data: units, backgroundColor: pal[1] }
      ]},
      options: { scales: { x: { stacked: true }, y: { stacked: true } } }
    };
  } else {
    revConfig = {
      type:'bar',
      data: { labels, datasets:[
        { type:'bar', label:'Ingresos', data: revs, backgroundColor: pal[0] },
        { type:'line', label:'Unidades', data: units, borderColor: pal[2], yAxisID:'y1' }
      ]},
      options: { scales: { y: { position:'left' }, y1: { position:'right', grid: { drawOnChartArea:false } } } }
    };
  }
  revConfig.options = Object.assign(baseOptions(maxSafe), revConfig.options||{});
  ui.charts.revenue = new Chart(ctxRev, revConfig);

  // Top products
  const ctxTop = document.getElementById('topProductsChart').getContext('2d');
  ui.charts.top = new Chart(ctxTop, {
    type:'bar',
    data: { labels: topLabels, datasets:[{ data: topVals, backgroundColor: topVals.map((_,i)=> pal[i%pal.length]) }] },
    options: Object.assign(baseOptions(Math.round(pctl(topVals,95)*1.1)), { indexAxis:'y', onClick: (_, els)=>{ if(!els.length) return; const i = els[0].index; selectProduct(topLabels[i]); } })
  });

  // Inventory
  const ctxI = document.getElementById('inventoryChart').getContext('2d');
  const invType = document.getElementById('invType').value;
  ui.charts.inv = new Chart(ctxI, {
    type: invType,
    data: { labels:Object.keys(inv), datasets:[{ data:Object.values(inv), backgroundColor:[pal[0], pal[1], pal[3]] }] },
    options: baseOptions(Math.max(...Object.values(inv),3))
  });

  // Aging
  const ctxA = document.getElementById('agingChart').getContext('2d');
  const agingType = document.getElementById('agingType').value;
  ui.charts.aging = new Chart(ctxA, {
    type: agingType,
    data: { labels:Object.keys(aging), datasets:[{ data:Object.values(aging), backgroundColor:[pal[1], pal[0], pal[2], pal[3]] }] },
    options: baseOptions(Math.max(...Object.values(aging),3))
  });

  // Trend
  renderProductTrend(null);
}

function selectProduct(name){
  document.getElementById('selectedProductLabel').textContent = name || '—';
  renderProductTrend(name);
}
function renderProductTrend(name){
  const pal = window.currentPalette;
  const rows = slice().filter(r => !name || r.product===name);
  const curr = document.getElementById('currency').value;
  const byDate = aggregateByDate(rows, curr);
  const labels = Object.keys(byDate);
  const revs = Object.values(byDate).map(v=>v.revenue);
  const units = Object.values(byDate).map(v=>v.units);
  const ctx = document.getElementById('productTrendChart').getContext('2d');
  if(ui.charts.trend) ui.charts.trend.destroy();
  ui.charts.trend = new Chart(ctx, {
    type:'line',
    data: { labels, datasets:[
      { label:'Ingresos', data: revs, borderColor: pal[0], backgroundColor: gradient(ctx, pal[0]+'BB', pal[0]+'05'), fill:true },
      { label:'Unidades', data: units, borderColor: pal[2], yAxisID:'y1' }
    ]},
    options: Object.assign(baseOptions(Math.round(pctl(revs,95)*1.1)), { scales: { y: { position:'left' }, y1: { position:'right', grid: { drawOnChartArea:false } } } })
  });
}

// Tables
function renderTables(){
  const search = (val, row) => Object.values(row).some(v => String(v).toLowerCase().includes(val.toLowerCase()));
  const sales = state.sales.slice(-150).reverse();
  const sTerm = document.getElementById('salesSearch').value || '';
  const sRows = sTerm ? sales.filter(r=>search(sTerm, r)) : sales;
  renderTable('salesTable', ['# Orden','Fecha','Canal','Región','SKU','Producto','Unidades','Monto'], sRows.map(r=>[r.order_id, r.date, r.channel, r.region, r.sku, r.product, r.units, r.amount]));

  const invoices = state.invoices.slice(-150).reverse();
  const iTerm = document.getElementById('invSearch').value || '';
  const iRows = iTerm ? invoices.filter(r=>search(iTerm, r)) : invoices;
  renderTable('invoicesTable', ['# Factura','Fecha','Cliente','Monto','Estado','Antigüedad'], iRows.map(r=>[r.invoice_id, r.date, r.customer, r.amount, r.status, r.aging_bucket]));

  renderTable('inventoryTable', ['SKU','Producto','Stock','Reorden','Estado'], state.inventory.map(r=>[r.sku, r.product, r.stock, r.reorder_point, r.status]));

  renderTable('productsTable', ['SKU','Producto','Categoría','Precio'], state.meta.products.map(p=>[p.sku, p.name, p.category, p.price]));
}
function renderTable(id, headers, rows){
  const el = document.getElementById(id);
  const thead = `<thead><tr>${headers.map(h=>`<th>${h}</th>`).join('')}</tr></thead>`;
  const tbody = `<tbody>${rows.map(r=>`<tr>${r.map(c=>`<td>${typeof c==='number'? c.toLocaleString('es-ES'): c}</td>`).join('')}</tr>`).join('')}</tbody>`;
  el.innerHTML = thead + tbody;
}

// Alerts
function renderAlerts(){
  const rows = slice();
  const lowMargin = rows.filter(r => (r.revenue - r.cost)/r.revenue < 0.1);
  const stockouts = state.inventory.filter(i=>i.status==='Stockout');
  const overdue = state.invoices.filter(i=>i.status==='Vencida');
  const items = [];
  if (lowMargin.length) items.push({ title:'Margen bajo detectado', desc:`${lowMargin.length} registros con margen < 10% en el rango seleccionado.` });
  if (stockouts.length) items.push({ title:'Stockouts', desc:`${stockouts.length} productos por debajo del mínimo.` });
  if (overdue.length) items.push({ title:'Facturas vencidas', desc:`${overdue.length} en mora.` });
  if (!items.length) items.push({ title:'Sin alertas', desc:'Todo estable en el periodo analizado.' });
  document.getElementById('alerts').innerHTML = items.map(a => `
    <div class="alert">
      <div class="title">${a.title}</div>
      <div class="desc">${a.desc}</div>
    </div>
  `).join('');
}

// Sparks
function spark(vals){
  if(!vals.length) return '';
  const w=120,h=36,p=4;
  const max=Math.max(...vals), min=Math.min(...vals);
  const scaleX=(i)=> p + i*(w-2*p)/(vals.length-1 || 1);
  const scaleY=(v)=> h-p - (max===min? 0.5 : (v-min)/(max-min))*(h-2*p);
  let d = vals.map((v,i)=> (i?'L':'M')+scaleX(i)+','+scaleY(v)).join(' ');
  return `<svg width="${w}" height="${h}"><path d="${d}" fill="none" stroke="currentColor" stroke-width="2"/></svg>`;
}

